import axios from 'axios';
import {selectFields} from '../utils/selectFields'

export const baseUrl =`https://hacker-news.firebaseio.com/v0/`;
export const topStoriesId =`${baseUrl}topstories.json?`;
export const StorycommentUrl = `${baseUrl}/item/`;



export const getStoryIds = async() =>{
    const result = await axios.get(topStoriesId).then( ({data}) => data);
    return result;
}

export const getStory = async(storyId) =>{
    const result = await axios
    .get(`${StorycommentUrl + storyId}.json`, console.log("---> ",`${StorycommentUrl + storyId}.json`))
    .then( ({data}) => selectFields(data));
    return result;
}

 //comments Ids

 export const getStoryComments = async(storyCommentsId) =>{
    const result = await axios
    .get(`${StorycommentUrl + storyCommentsId}.json`, console.log("---> ",`${StorycommentUrl+ storyCommentsId}.json`))
    .then( ({data}) => selectFields(data));
    return result;
}
