import axios from 'axios';

export const baseUrl =`https://hacker-news.firebaseio.com/v0/`;
export const topStoriesId =`${baseUrl}topstories.json?`;
export const StoryItem = `${baseUrl}/item/`;

//https://hacker-news.firebaseio.com/v0/item/22212619.json

export const getStoryKids = async(storyIdKids) =>{
    const result = await axios.get(`${StoryItem + storyIdKids}.json`).then(({data}) =>(data));
    return result
}

export const getStory = async(storyId) =>{
    const result = await axios.get(`${StoryItem + storyId}.json`)
    .then(res =>{
        var story = res.data;
        story.comments =[];
        res.map(storyId => {
            axios.get(`${StoryItem + storyId}.json`)
            .then(res =>{
                console.log("-",res.kids);
            });
        });

        return story;
    });

    return result
}
export const getStoryIds = async() =>{
    const result = await axios.get(topStoriesId).then( ({data}) => data);
    return result;
}
