import axios from 'axios';

export const baseUrl =`https://hacker-news.firebaseio.com/v0/`;
export const topStoriesUrl =`${baseUrl}topstories.json?limit=2`;
export const commentsUrl = `${baseUrl}item/`;


export const getStoryIds = async() =>{
    const result = await axios.get(topStoriesUrl).then( ({data}) => data.splice());
    return result;
}
