import axios from 'axios';
import {selectFields} from '../utils/selectFields'

export const baseUrl =`https://hacker-news.firebaseio.com/v0/`;
export const topStoriesId =`${baseUrl}topstories.json?`;
export const Storycomment = `${baseUrl}item/`;



export const getStoryIds = async() =>{
    const result = await axios.get(topStoriesId).then( ({data}) => data);
    return result;
}

export const getStory = async(storyId) =>{
    const result = await axios
    .get(`${Storycomment + storyId}.json`, console.log(`${Storycomment }.json`))
    .then( ({data}) => selectFields(data));
    return result;
}

