import React from 'react';
import {useState, useEffect} from 'react';
import { getStory, getStoryKids} from '../services/hnApi';
export const Story = ({ storyId,commentId }) => {
    const [story, setStory] = useState({});
    console.log("-->", storyId);
    useEffect(() => {
       getStory(storyId).then(
           ({data}) => data &&  console.log("x-> ", setStory(data))
           );
    //    getStory(commentId).then(
    //         data => data &&  setStory(data)
    //         );    

    }, []);

    return story && story.url ? (
        <div>
            <p>{ story.title }</p>
            <ul>
                <li> { story.kids[0].text} <br/> </li>
                <li> { story.kids[1] } <br/> </li>
            </ul>
        </div>
    ): null ;
};
