import React from 'react';
import {useState, useEffect} from 'react';
import { getStory, getStoryKids} from '../services/hnApi';
export const Story = ({ storyId }) => {
    const [story, setStory] = useState({});

    useEffect(() => {
       getStory(storyId).then(data => data && data.comments && setStory(data));

    }, []);

    return story && story.kids ? (
        <div>
            <p>{ story.title }</p>
            <ul>
                <li> { story.comments } <br/> </li>
                <li> { story.kids[1] } <br/> </li>
            </ul>
        </div>
    ): null ;
};
