import axios from 'axios';

export const baseUrl =`https://hacker-news.firebaseio.com/v0/`;
export const topStoriesId =`${baseUrl}topstories.json?`;
export const StoryItem = `${baseUrl}/item/`;

export const StoryComments =`${baseUrl}/item/`
console.log(StoryComments);

//https://hacker-news.firebaseio.com/v0/item/22212619.json

export const getStoryComments = async(storyIdComments) =>{
    const result = await axios.get(`${StoryComments + storyIdComments}.json`);
    //console.log("StoryIdComments -> ",result);
    return result;
}
export const getStory = async(storyId) =>{
    const result = await axios
    .get(`${StoryItem + storyId}.json`)
    .then( ({data}) =>(data));
    console.log("--> ",result);
    return result;
}
export const getStoryIds = async() =>{
    const result = await axios.get(topStoriesId).then( ({data}) => data);
    return result;
}
